
docker service create   --name client   --mode global   --publish mode=host,target=5000,published=5000   --mount type=bind,source=/sys/class/net/eth0/address,target=/client/src/config/address   --mount type=volume,source=cluster_test_app_sqlite3,target=/client/db   --env MAC_ADDRESS_FILE=/client/src/config/address   --network cluster_test_app   jabaridash/cluster_test_app_client
docker service create   --name database   --publish 5432:5432   --replicas=1   --constraint 'node.role == manager'   --mount type=volume,source=cluster_test_app_database,target=/var/lib/postgresql/data   --secret DEFAULT_PASSWORD   --env POSTGRES_PASSWORD_FILE=/run/secrets/DEFAULT_PASSWORD   --network cluster_test_app   jabaridash/cluster_test_app_database
docker service create   --name server   --publish 8085:8085   --replicas=1   --constraint 'node.role == manager'   --secret DEFAULT_PASSWORD   --env DB_PASSWORD_FILE=/run/secrets/DEFAULT_PASSWORD   --network cluster_test_app   jabaridash/cluster_test_app_server
